function trajectoryTrackingFigure(X1, Y1, X2, Y2)
%CREATEFIGURE(X1,Y1,X2,Y2)
%  X1:  vector of x data
%  Y1:  vector of y data
%  X2:  vector of x data
%  Y2:  vector of y data

%  Auto-generated by MATLAB on 11-Jan-2013 11:32:22

% Create figure
figure1 = figure('Color',[1 1 1]);

% Create axes
axes1 = axes('Parent',figure1,'PlotBoxAspectRatio',[13 3.5 1],...
    'DataAspectRatio',[1 1 1]);

box(axes1,'on');
hold(axes1,'all');

% Create plot
plot(X1,Y1,'Parent',axes1,'LineWidth',2,'DisplayName','Actual Trajectory');

% Create plot
plot(X2,Y2,'Parent',axes1,'LineWidth',2,'LineStyle','-.','Color',[1 0 0],...
    'DisplayName','Desired Trajectory');

% Create xlabel
xlabel('x(m)');

% Create ylabel
ylabel('y(m)');

% Create legend
